mapper.select:
select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status from hr_confirm c, hr_employees e, hr_department d where c.from_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'N' and to_emp_no = #{empNo}
select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status from hr_confirm c, hr_employees e, hr_department d where c.to_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'N' and from_emp_no = #{empNo}
order by con_date desc

select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status, c.con_check_date from hr_confirm c, hr_employees e, hr_department d where c.from_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'Y' and to_emp_no = #{empNo}
select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status from hr_confirm c, hr_employees e, hr_department d where c.to_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'Y' and from_emp_no = #{empNo}
order by con_check_date desc

select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status from hr_confirm c, hr_employees e, hr_department d where c.from_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'Z' and to_emp_no = #{empNo}
select c.con_no, c.con_title, e.emp_name as fromName, d.dept_name as fromDept, c.con_date, c.con_status from hr_confirm c, hr_employees e, hr_department d where c.to_emp_no = e.emp_no and e.DEPT_CODE = d.DEPT_CODE and con_status = 'Z' and from_emp_no = #{empNo}
order by con_check_date desc

mapper.update:
update hr_confirm
set con_status = 'Y', con_check_date = current_timestamp
where con_no = #{conNo};

update hr_confirm
set con_status = 'Z', con_check_date = current_timestamp
where con_no = #{conNo};
