mapper.select:
SELECT E.mail_no, P.emp_email, P.emp_name, P.role_code, P.dept_code, E.mail_title, E.mail_status, E.mail_date FROM HR_EMAIL AS E JOIN HR_EMPLOYEES AS P
ON E.MAIL_TO = P.EMP_NO WHERE E.mail_to = #{empNo}
ON E.MAIL_FROM = P.EMP_NO WHERE E.mail_from = #{empNo}
ORDER BY E.mail_no DESC LIMIT #{s.pageLimitNum},#{s.mailAmount}

mapper.update:
UPDATE hr_email
SET mail_status = 'Y'
WHERE mail_no = #{mailNo};

mapper.select:
SELECT COUNT(*)
FROM hr_email


        WHERE mail_to = #{empNo}


        WHERE mail_from = #{empNo}
